contracts/BridgePart.sol.content:
// SPDX-License-Identifier: NONE 

/*
IMPORTANT
--Properitary Software -- Do Not Distribute or Legal Action will be taken on--

BUILT SPECIFICALLY FOR PARTNERSHIP BETWEEN GRAYZONE AND BRIDGE PROTOCOL
--- Further mantainence and upgrades to the system manage by GRAYZONE 

V1 Specs : Native and ERC20 Bridge to EVMs 
         : Affiliate system with 20% bridge revenue share from refered accounts
         : Auto Distribution of Grayzone's Share to Grayzone's Treasury

IMPORTANT---
Post Deployment allow all endpoins for all instances to recieve bridges from
*/
pragma solidity ^0.8.17;

import "./src/Utils.sol";
import {IWETH} from "./src/interfaces/IWETH.sol";
import {TokenBase, TokenReceiver, TokenSender} from "./src/TokenBase.sol";
import "./src/interfaces/IWormholeReceiver.sol";
import "./src/interfaces/IWormholeRelayer.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol"; // use oz < 5.0

contract BridgeV1 is TokenSender , TokenReceiver, Ownable{
    
    uint256 public fee = 0.001 ether; // dst chain fee
    uint256 public fMultiplier = 5; // default 0.5%
    address public solutionsProviderAddress; // Partner Address
    uint256 public solutionsProviderShare = 19; // 19% of fee generated
    uint256 public totalPoints; // total points gathered by all addresses

    bool public bridgeActive = true; // state to control if bridging is allowed

    struct USER {
        uint256 nonces;
        uint256 sendNonces;
        uint256 points; // this points represent the point per tx , volume based calculations must be done to add to this variable for end points
        uint256 totalReferals;
        mapping(uint256 => address) _referals; // count to address
        mapping(bytes32=> Bridges) bridges;
        mapping(uint256=>mapping(uint16 =>bytes32)) hashes;
    }

    struct Referals{
        bool refered;
        address referer;
    }

    struct Bridges {
        uint16 fromChain;
        uint8 sendType;
        address token;
        uint256 amount;
        bool claimed;
    }

    mapping(address => mapping(address => uint256)) public totalUserVolume; // used for point calculation mechanism on extension contracts
    mapping(address => Referals) internal referals; // to check if user has beed refered already
    mapping(address => uint256 ) public feesTotal; // contract/protocol fee
    mapping(address => USER) internal userData; // userData
    mapping(uint16 => mapping(address => bool)) public endpoints;

    constructor(
        address _wormholeRelayer, // wormhole relayer address
        address _tokenBridge, // wormhole token bridge address
        address _wormhole, // wormhole core address
        address grayzoneAddress // partnership % sharing address
    
    ) TokenBase(_wormholeRelayer, _tokenBridge, _wormhole) {
        solutionsProviderAddress = grayzoneAddress;
    }
    // returns [user bridge in nonces, bridge Out Nonces , user points, user total referals]
    function getUserData(address user) public view returns (uint256 , uint256, uint256, uint256){
        return (userData[user].nonces,userData[user].sendNonces , userData[user].points,userData[user].totalReferals);
    }
    // returns user's refered addresses
    function getUserReferals(address user , uint nonce) public view returns(address){
        return (userData[user]._referals[nonce]);
    }
    // returns if a user is under referal or og
    function getUserReferalStat(address user) public view returns(Referals memory){
        return referals[user];
    }
    // returns hash of bridges for nonces and chain id
    function getUserBridgeHashes(address user, uint256 nonce, uint16 chainId)public view returns(bytes32){
        return userData[user].hashes[nonce][chainId];
    }
    // returns bridge data for hash for user
    function getUserBridges(address user ,bytes32 hash) public view returns(Bridges memory){
        return userData[user].bridges[hash];
    }
    
    // get bridge fee quotations (amount eth need to send along bridgeFunctions) -- keep amount == 0 for erc20 transfers , amount = ether/native to send to dest for eth bridges + fee needed 
    // Returns total eth needed to send to function

    function quoteCrossChainDeposit(
        uint16 targetChain,
        uint256 amt ,
        uint256 gasUnits
    ) public view returns (uint256 cost, uint256 f) {
        uint256 deliveryCost;
        f = fee;
        (deliveryCost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            amt,
            gasUnits
        );
        // Total cost: delivery cost + cost of publishing the 'sending token' wormhole message
        cost = deliveryCost + wormhole.messageFee() + f;
    }
    //function to set allowed enpoints of bridge , only allow data from those contracts 

    function setAllowedEndpoint (uint16 chainId , address bridgeContract) public onlyOwner{
        endpoints[chainId][bridgeContract] = true;
    }
    //claim failed 
    function claimFailedBridge(bytes32 deliveryhash) public {
        Bridges memory bridge = userData[msg.sender].bridges[deliveryhash];
        bool cl; uint256 amt ; address tkn;
        cl = bridge.claimed;
        amt = bridge.amount;
        tkn = bridge.token;
        if(bridge.sendType == uint8(0) && amt > 0 && cl == false){
            payable(msg.sender).transfer(amt);
        }
        if(bridge.sendType == uint8(1)&&amt > 0 && cl == false){
           IERC20(tkn).transfer(msg.sender , amt);
        }
    }

    // bridge revenue withdrawal
    function withdrawRevenue(address token , uint256 amt, address to) public onlyOwner{
        require(feesTotal[token]>= amt,"enter correct val");
        feesTotal[token] = feesTotal[token] - amt;
        if(token == address(0)){
            payable(to).transfer(amt);
        }
        else { 
            IERC20(token).transfer(to , amt);
        }
    }
    // base fee for bridges 
    function changeBaseFee(uint256 wEi) public onlyOwner {
        fee = wEi;
    }
    // % based fee on bridged amount
    function changePercentFee(uint256 fMultiplier_) public onlyOwner{
        require(fMultiplier_ < 101,"cannot exceed 10%");
        fMultiplier = fMultiplier_;
    }

    // bridge erc20 -- ref == address(0) in case of a non-reffered user
    function bridgeErc20(
        uint16 targetChain,
        address targetBridge,
        address recipient,
        uint256 amount,
        address token,
        uint256 dstGas,
        address ref
    ) public payable {
        (uint256 cost, uint256 f) = quoteCrossChainDeposit(targetChain, 0, dstGas);
        require(
            msg.value == cost,
            "msg.value must be == quoteCrossChainDeposit(targetChain)"
        );
        bool x = IERC20(token).transferFrom(msg.sender, address(this), amount);
        require(x ,"failed");
        //handle provider distribution
        (f,)= _processPartner(f);
        bytes memory payload = abi.encode(recipient, uint8(1) , 0);
        sendTokenWithPayloadToEvm(
            targetChain,
            targetBridge, // address (on targetChain) to send token and payload to
            payload,
            0,
            dstGas, // gas units
            token, // address of IERC20 token contract
            amount // amount
        );
        totalUserVolume[msg.sender][token] = totalUserVolume[msg.sender][token] + amount;
        //handle referals
        _handleRef(msg.sender , ref);
        //handle points
        _processPoints(msg.sender, ref);
        userData[msg.sender].sendNonces = userData[msg.sender].sendNonces + 1;
        feesTotal[address(0)] =feesTotal[address(0)] + f;
    }

    // bridge native
    function bridgeEther(
        uint16 targetChain,
        address targetBridge,
        address recipient,
        uint256 amount
        ,uint256 dstGas,
        address ref 
    ) public payable {
        (uint256 cost, uint256 f) = quoteCrossChainDeposit(targetChain, amount,dstGas);
        require(
            msg.value >= cost,
            "amount must be == quoteCrossChainDeposit(targetChain)"
        );
        //handle provider distribution
        IWETH weth = tokenBridge.WETH();
        ( f,) = _processPartner(f);
        bytes memory payload = abi.encode(recipient ,uint8(0), amount);
        sendTokenWithPayloadToEvm(
            targetChain,
            targetBridge, // address (on targetChain) to send token and payload to
            payload, // data
            amount, // receiver value
            dstGas, // gas units
            address(weth), // address of IERC20 token contract
            0 // amount 0 as native send as gas drop
        );
        totalUserVolume[msg.sender][address(0)] = totalUserVolume[msg.sender][address(0)] + amount;
        _handleRef(msg.sender, ref);
        _processPoints(msg.sender,ref);
        userData[msg.sender].sendNonces = userData[msg.sender].sendNonces + 1;
        feesTotal[address(0)] =feesTotal[address(0)] + f;
    }

    // overrides and delivery Functions -- atleast 600_000 gas units for normal chains , 3mil for op-arb
    function receivePayloadAndTokens(
        bytes memory payload,
        TokenReceived[] memory receivedTokens,
        bytes32 srcA, // sourceAddress
        uint16 ch,
        bytes32 dh// deliveryHash
    ) internal override onlyWormholeRelayer {
        require(receivedTokens.length == 1);
        address bridge = fromWormholeFormat(srcA);
        require(endpoints[ch][bridge]== true); // checks is endpoint is allowed and tokens sent is not blank
        (
            address recipient,
            uint8 type_,
            uint256 amt
        ) = abi.decode(payload, (address, uint8 , uint256));

        require(userData[recipient].bridges[dh].claimed == false);
        address tokenAddress = receivedTokens[0].tokenAddress;
        uint256 userN = userData[recipient].nonces;
        userData[recipient].nonces = userN + 1;
        // type handling for native and erc20
        if(type_ == uint8(0)) { 
            uint256 a_ = amt * fMultiplier/1000;// fee
            uint256 f_ = amt - a_; // d amt 
            Bridges memory br = Bridges({
                fromChain :ch,
                sendType : type_,
                token: tokenAddress,
                amount: f_,
                claimed: true
            });
            userData[recipient].bridges[dh]= br;
            userData[recipient].hashes[userN][ch] = dh;
            feesTotal[address(0)] =feesTotal[address(0)] + a_;
            (bool success, ) = recipient.call{value: f_}(new bytes(0));
            if(!success){userData[recipient].bridges[dh].claimed = false;}
            
        } else  if(type_ == uint8(1)){
            uint256 amount = receivedTokens[0].amount;
            uint256 f_=amount * fMultiplier/ 1000;
            uint256 a = amount - f_;
            Bridges memory br = Bridges({
                fromChain :ch,
                sendType : type_,
                token: tokenAddress,
                amount: a,
                claimed: true
            });
            userData[recipient].bridges[dh]= br;
            userData[recipient].hashes[userN][ch] = dh;
            feesTotal[tokenAddress] =feesTotal[tokenAddress] + f_;
            bool x =IERC20(tokenAddress).transfer(recipient,a);
            if(!x){userData[recipient].bridges[dh].claimed = false;}
        } 
    }

    //only grayzone can set the address to which they recieve profit sharing 
    function setProfitShareAddr(address addr) public {
        require(msg.sender == solutionsProviderAddress,"Only Grayzone allowed");
        solutionsProviderAddress = addr;
    }
    //grayzone is free to change the % they recieve at any time , can only decrease from max 19%
    function setProfitSharePercent(uint256 multiplier) public {
        require(multiplier < 20 ,"Can only be max 19 %"); // < 20 means 19
        require(msg.sender == solutionsProviderAddress,"Only Grayzone allowed");
        solutionsProviderShare = multiplier;
    }

    //process points for referer and refered 
    function _processPoints(address refered , address referer) internal {
        userData[refered].points =userData[refered].points + 1 ;
        if(referer != address(0)){
            userData[referer].points =userData[referer].points + 1 ;
        }
        totalPoints ++;
    }

    // internal initialize function for first referals -- checks if the user is refered before and if user is a fresh user
    function _handleRef (address refered , address ref) internal {
        if(ref != address(0) && referals[refered].refered == false && userData[refered].sendNonces == 0){
            referals[refered].refered = true;
            referals[refered].referer = ref;
            uint f__ = userData[ref].totalReferals;
            userData[ref].totalReferals = f__+1;
            userData[ref]._referals[f__] = msg.sender; 
        }
    }
    // internal process function to handle partner shares 
    function _processPartner(uint256 f) internal returns(uint256 , uint256){
        uint256 rf = f * solutionsProviderShare / 100;
        payable(solutionsProviderAddress).transfer(rf);
        return(f - rf , rf);
    }
    // emergency function for user point data
    function setUserPoints(address user , uint256 p) public onlyOwner {
        userData[user].points = p;
    }
    // emergency function to withdraw tokens from failed bridges
    function unsafeWithdraw(address token , uint256 amt, bool isFee) public onlyOwner{
        require(bridgeActive == false,"Only allowed if bridge inactive for users");
        if(isFee == true){
            feesTotal[token] = feesTotal[token] - amt;
        }
        if(token == address(0)){
            payable(msg.sender).transfer(amt);
        }
        else { 
            IERC20(token).transfer(msg.sender , amt);
        }
    }
    // function to toggle bridge active status , bridge must be turned off to execute unsafe withdrawals
    function toggleState(bool state) public onlyOwner{
        bridgeActive = state;
    }
}

contracts/BridgeSimple.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "./src/Utils.sol";
import {IWETH} from "./src/interfaces/IWETH.sol";
import {TokenBase, TokenReceiver, TokenSender} from "./src/TokenBase.sol";
import "./src/interfaces/IWormholeReceiver.sol";
import "./src/interfaces/IWormholeRelayer.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol"; // use oz < 5.0

contract BridgeV1 is TokenSender , TokenReceiver, Ownable{

//    mapping(address => USER) internal userData;
    mapping(address=>mapping(bytes32=> bool)) public delivered;
    mapping(uint16 => mapping(address => bool)) public endpoints;

    constructor(
        address _wormholeRelayer,
        address _tokenBridge,
        address _wormhole//,
    
    ) TokenBase(_wormholeRelayer, _tokenBridge, _wormhole) {
        
    }
    // allow contract instances must from src chains
    function setEndpointForRecieve(uint16 ch , address Address) public onlyOwner {
        endpoints[ch][Address] = true;
    }

    function quoteCrossChainDeposit(
        uint16 targetChain,
        uint256 amt ,
        uint256 gasUnits
    ) public view returns (uint256 cost) {
        uint256 deliveryCost;
        //f = fee;
        (deliveryCost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            amt,
            gasUnits
        );
        // Total cost: delivery cost + cost of publishing the 'sending token' wormhole message + dst fee
        cost = deliveryCost + wormhole.messageFee();
    }

    function bridgeErc20(
        uint16 targetChain,
        address targetHelloToken,
        address recipient,
        uint256 amount,
        address token,
        uint256 dstGas
    ) public payable {
        (uint256 cost) = quoteCrossChainDeposit(targetChain, 0, dstGas);
        require(
            msg.value >= cost,
            "msg.value must be quoteCrossChainDeposit(targetChain)"
        );
        IERC20(token).transferFrom(msg.sender, address(this), amount);

        bytes memory payload = abi.encode(recipient, 1 , 0);
        sendTokenWithPayloadToEvm(
            targetChain,
            targetHelloToken, // address (on targetChain) to send token and payload to
            payload,
            0, // receiver value
            dstGas, // gas units
            token, // address of IERC20 token contract
            amount // amount
        );
    }

    function bridgeEther(
        uint16 targetChain,
        address targetHelloToken,
        address recipient,
        uint256 amount
        ,uint256 dstGas
    ) public payable {
        (uint256 cost) = quoteCrossChainDeposit(targetChain, amount,dstGas);
        require(
            msg.value >= cost,
            "mismatch in fee"
        );
        IWETH wrappedNativeToken = tokenBridge.WETH();
        bytes memory payload = abi.encode(recipient ,uint8(0), amount);
        sendTokenWithPayloadToEvm(
            targetChain,
            targetHelloToken, // address (on targetChain) to send token and payload to
            payload, // data
            amount, // receiver value 
            dstGas, // gas units
            address(wrappedNativeToken), // address of IERC20 token contract
            0 // amount 0 as native send as gas drop
        );
    }

    function receivePayloadAndTokens(
        bytes memory payload,
        TokenReceived[] memory receivedTokens,
        bytes32 srcA, // sourceAddress
        uint16 ch,
        bytes32 dh// deliveryHash
    ) internal override onlyWormholeRelayer {
        require(receivedTokens.length == 1);
        require(endpoints[ch][fromWormholeFormat(srcA)] == true); // to prevent any contract to mock data and drain fee or other balances
        (
            address recipient,
            uint8 type_,
            uint256 amt
        ) = abi.decode(payload, (address, uint8, uint256));

        require(delivered[recipient][dh] == false); // to prevent reentrancy and spoofing attacks
        address tokenAddress = receivedTokens[0].tokenAddress;
        // type handling for native and erc20
        if(type_ == uint8(0)) { 
            payable(recipient).transfer(amt);
            delivered[recipient][dh] = true; 
        } else  if(type_ == uint8(1)){
            uint256 amount = receivedTokens[0].amount;
            IERC20(tokenAddress).transfer(recipient,amount);
            delivered[recipient][dh] = true;
        }
    }

}

contracts/src/Base.sol.content:
pragma solidity ^0.8.13;

import "./interfaces/IWormholeReceiver.sol";
import "./interfaces/IWormholeRelayer.sol";
import "./interfaces/IWormhole.sol";
import "./Utils.sol";

abstract contract Base {
    IWormholeRelayer public immutable wormholeRelayer;
    IWormhole public immutable wormhole;

    address registrationOwner;
    mapping(uint16 => bytes32) registeredSenders;

    constructor(address _wormholeRelayer, address _wormhole) {
        wormholeRelayer = IWormholeRelayer(_wormholeRelayer);
        wormhole = IWormhole(_wormhole);
        registrationOwner = msg.sender;
    }

    modifier onlyWormholeRelayer() {
        require(
            msg.sender == address(wormholeRelayer),
            "Msg.sender is not Wormhole Relayer"
        );
        _;
    }

    modifier isRegisteredSender(uint16 sourceChain, bytes32 sourceAddress) {
        require(
            registeredSenders[sourceChain] == sourceAddress,
            "Not registered sender"
        );
        _;
    }

    /**
     * Sets the registered address for 'sourceChain' to 'sourceAddress'
     * So that for messages from 'sourceChain', only ones from 'sourceAddress' are valid
     *
     * Assumes only one sender per chain is valid
     * Sender is the address that called 'send' on the Wormhole Relayer contract on the source chain)
     */
    function setRegisteredSender(
        uint16 sourceChain,
        bytes32 sourceAddress
    ) public {
        require(
            msg.sender == registrationOwner,
            "Not allowed to set registered sender"
        );
        registeredSenders[sourceChain] = sourceAddress;
    }
}

contracts/src/CCTPAndTokenBase.sol.content:
pragma solidity ^0.8.13;

import "./interfaces/IWormholeReceiver.sol";
import "./interfaces/IWormholeRelayer.sol";
import "./interfaces/ITokenBridge.sol";
import {IERC20} from "./interfaces/IERC20.sol";
import "./interfaces/CCTPInterfaces/ITokenMessenger.sol";
import "./interfaces/CCTPInterfaces/IMessageTransmitter.sol";

import "./Utils.sol";
import "./TokenBase.sol";
import "./CCTPBase.sol";

abstract contract CCTPAndTokenBase is CCTPBase {
    ITokenBridge public immutable tokenBridge;

    enum Transfer {
        TOKEN_BRIDGE,
        CCTP
    }

    constructor(
        address _wormholeRelayer,
        address _tokenBridge,
        address _wormhole,
        address _circleMessageTransmitter,
        address _circleTokenMessenger,
        address _USDC
    )
        CCTPBase(
            _wormholeRelayer,
            _wormhole,
            _circleMessageTransmitter,
            _circleTokenMessenger,
            _USDC
        )
    {
        tokenBridge = ITokenBridge(_tokenBridge);
    }
}

abstract contract CCTPAndTokenSender is CCTPAndTokenBase {
    // CCTP Sender functions, taken from "./CCTPBase.sol"

    uint8 internal constant CONSISTENCY_LEVEL_FINALIZED = 15;

    using CCTPMessageLib for *;

    mapping(uint16 => uint32) public chainIdToCCTPDomain;

    /**
     * Sets the CCTP Domain corresponding to chain 'chain' to be 'cctpDomain'
     * So that transfers of USDC to chain 'chain' use the target CCTP domain 'cctpDomain'
     *
     * This action can only be performed by 'cctpConfigurationOwner', who is set to be the deployer
     *
     * Currently, cctp domains are:
     * Ethereum: Wormhole chain id 2, cctp domain 0
     * Avalanche: Wormhole chain id 6, cctp domain 1
     * Optimism: Wormhole chain id 24, cctp domain 2
     * Arbitrum: Wormhole chain id 23, cctp domain 3
     * Base: Wormhole chain id 30, cctp domain 6
     *
     * These can be set via:
     * setCCTPDomain(2, 0);
     * setCCTPDomain(6, 1);
     * setCCTPDomain(24, 2);
     * setCCTPDomain(23, 3);
     * setCCTPDomain(30, 6);
     */
    function setCCTPDomain(uint16 chain, uint32 cctpDomain) public {
        require(
            msg.sender == cctpConfigurationOwner,
            "Not allowed to set CCTP Domain"
        );
        chainIdToCCTPDomain[chain] = cctpDomain;
    }

    function getCCTPDomain(uint16 chain) internal view returns (uint32) {
        return chainIdToCCTPDomain[chain];
    }

    /**
     * transferUSDC wraps common boilerplate for sending tokens to another chain using IWormholeRelayer
     * - approves the Circle TokenMessenger contract to spend 'amount' of USDC
     * - calls Circle's 'depositForBurnWithCaller'
     * - returns key for inclusion in WormholeRelayer `additionalVaas` argument
     *
     * Note: this requires that only the targetAddress can redeem transfers.
     *
     */

    function transferUSDC(
        uint256 amount,
        uint16 targetChain,
        address targetAddress
    ) internal returns (MessageKey memory) {
        IERC20(USDC).approve(address(circleTokenMessenger), amount);
        bytes32 targetAddressBytes32 = addressToBytes32CCTP(targetAddress);
        uint64 nonce = circleTokenMessenger.depositForBurnWithCaller(
            amount,
            getCCTPDomain(targetChain),
            targetAddressBytes32,
            USDC,
            targetAddressBytes32
        );
        return
            MessageKey(
                CCTPMessageLib.CCTP_KEY_TYPE,
                abi.encodePacked(getCCTPDomain(wormhole.chainId()), nonce)
            );
    }

    // Publishes a CCTP transfer of 'amount' of USDC
    // and requests a delivery of the transfer along with 'payload' to 'targetAddress' on 'targetChain'
    //
    // The second step is done by publishing a wormhole message representing a request
    // to call 'receiveWormholeMessages' on the address 'targetAddress' on chain 'targetChain'
    // with the payload 'abi.encode(Transfer.CCTP, amount, payload)'
    // (we encode a Transfer enum to distinguish this from a TokenBridge transfer)
    // (and we encode the amount so it can be checked on the target chain)
    function sendUSDCWithPayloadToEvm(
        uint16 targetChain,
        address targetAddress,
        bytes memory payload,
        uint256 receiverValue,
        uint256 gasLimit,
        uint256 amount
    ) internal returns (uint64 sequence) {
        MessageKey[] memory messageKeys = new MessageKey[](1);
        messageKeys[0] = transferUSDC(amount, targetChain, targetAddress);

        bytes memory userPayload = abi.encode(Transfer.CCTP, amount, payload);
        address defaultDeliveryProvider = wormholeRelayer
            .getDefaultDeliveryProvider();

        (uint256 cost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            receiverValue,
            gasLimit
        );

        sequence = wormholeRelayer.sendToEvm{value: cost}(
            targetChain,
            targetAddress,
            userPayload,
            receiverValue,
            0,
            gasLimit,
            targetChain,
            address(0x0),
            defaultDeliveryProvider,
            messageKeys,
            CONSISTENCY_LEVEL_FINALIZED
        );
    }

    function addressToBytes32CCTP(address addr) private pure returns (bytes32) {
        return bytes32(uint256(uint160(addr)));
    }

    // TokenBridge Sender functions, taken from "./TokenBase.sol"

    /**
     * transferTokens wraps common boilerplate for sending tokens to another chain using IWormholeRelayer
     * - approves tokenBridge to spend 'amount' of 'token'
     * - emits token transfer VAA
     * - returns VAA key for inclusion in WormholeRelayer `additionalVaas` argument
     *
     * Note: this function uses transferTokensWithPayload instead of transferTokens since the former requires that only the targetAddress
     *       can redeem transfers. Otherwise it's possible for another address to redeem the transfer before the targetContract is invoked by
     *       the offchain relayer and the target contract would have to be hardened against this.
     *
     */
    function transferTokens(
        address token,
        uint256 amount,
        uint16 targetChain,
        address targetAddress
    ) internal returns (VaaKey memory) {
        return
            transferTokens(
                token,
                amount,
                targetChain,
                targetAddress,
                bytes("")
            );
    }

    /**
     * transferTokens wraps common boilerplate for sending tokens to another chain using IWormholeRelayer.
     * A payload can be included in the transfer vaa. By including a payload here instead of the deliveryVaa,
     * fewer trust assumptions are placed on the WormholeRelayer contract.
     *
     * - approves tokenBridge to spend 'amount' of 'token'
     * - emits token transfer VAA
     * - returns VAA key for inclusion in WormholeRelayer `additionalVaas` argument
     *
     * Note: this function uses transferTokensWithPayload instead of transferTokens since the former requires that only the targetAddress
     *       can redeem transfers. Otherwise it's possible for another address to redeem the transfer before the targetContract is invoked by
     *       the offchain relayer and the target contract would have to be hardened against this.
     */
    function transferTokens(
        address token,
        uint256 amount,
        uint16 targetChain,
        address targetAddress,
        bytes memory payload
    ) internal returns (VaaKey memory) {
        IERC20(token).approve(address(tokenBridge), amount);
        uint64 sequence = tokenBridge.transferTokensWithPayload{
            value: wormhole.messageFee()
        }(
            token,
            amount,
            targetChain,
            toWormholeFormat(targetAddress),
            0,
            payload
        );
        return
            VaaKey({
                emitterAddress: toWormholeFormat(address(tokenBridge)),
                chainId: wormhole.chainId(),
                sequence: sequence
            });
    }

    // Publishes a wormhole message representing a 'TokenBridge' transfer of 'amount' of 'token'
    // and requests a delivery of the transfer along with 'payload' to 'targetAddress' on 'targetChain'
    //
    // The second step is done by publishing a wormhole message representing a request
    // to call 'receiveWormholeMessages' on the address 'targetAddress' on chain 'targetChain'
    // with the payload 'abi.encode(Transfer.TOKEN_BRIDGE, payload)'
    // (we encode a Transfer enum to distinguish this from a CCTP transfer)
    function sendTokenWithPayloadToEvm(
        uint16 targetChain,
        address targetAddress,
        bytes memory payload,
        uint256 receiverValue,
        uint256 gasLimit,
        address token,
        uint256 amount
    ) internal returns (uint64) {
        VaaKey[] memory vaaKeys = new VaaKey[](1);
        vaaKeys[0] = transferTokens(token, amount, targetChain, targetAddress);

        (uint256 cost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            receiverValue,
            gasLimit
        );
        return
            wormholeRelayer.sendVaasToEvm{value: cost}(
                targetChain,
                targetAddress,
                abi.encode(Transfer.TOKEN_BRIDGE, payload),
                receiverValue,
                gasLimit,
                vaaKeys
            );
    }

    function sendTokenWithPayloadToEvm(
        uint16 targetChain,
        address targetAddress,
        bytes memory payload,
        uint256 receiverValue,
        uint256 gasLimit,
        address token,
        uint256 amount,
        uint16 refundChain,
        address refundAddress
    ) internal returns (uint64) {
        VaaKey[] memory vaaKeys = new VaaKey[](1);
        vaaKeys[0] = transferTokens(token, amount, targetChain, targetAddress);

        (uint256 cost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            receiverValue,
            gasLimit
        );
        return
            wormholeRelayer.sendVaasToEvm{value: cost}(
                targetChain,
                targetAddress,
                abi.encode(Transfer.TOKEN_BRIDGE, payload),
                receiverValue,
                gasLimit,
                vaaKeys,
                refundChain,
                refundAddress
            );
    }
}

abstract contract CCTPAndTokenReceiver is CCTPAndTokenBase {
    function redeemUSDC(
        bytes memory cctpMessage
    ) internal returns (uint256 amount) {
        (bytes memory message, bytes memory signature) = abi.decode(
            cctpMessage,
            (bytes, bytes)
        );
        uint256 beforeBalance = IERC20(USDC).balanceOf(address(this));
        circleMessageTransmitter.receiveMessage(message, signature);
        return IERC20(USDC).balanceOf(address(this)) - beforeBalance;
    }

    struct TokenReceived {
        bytes32 tokenHomeAddress;
        uint16 tokenHomeChain;
        address tokenAddress; // wrapped address if tokenHomeChain !== this chain, else tokenHomeAddress (in evm address format)
        uint256 amount;
        uint256 amountNormalized; // if decimals > 8, normalized to 8 decimal places
    }

    function getDecimals(
        address tokenAddress
    ) internal view returns (uint8 decimals) {
        // query decimals
        (, bytes memory queriedDecimals) = address(tokenAddress).staticcall(
            abi.encodeWithSignature("decimals()")
        );
        decimals = abi.decode(queriedDecimals, (uint8));
    }

    function getTokenAddressOnThisChain(
        uint16 tokenHomeChain,
        bytes32 tokenHomeAddress
    ) internal view returns (address tokenAddressOnThisChain) {
        return
            tokenHomeChain == wormhole.chainId()
                ? fromWormholeFormat(tokenHomeAddress)
                : tokenBridge.wrappedAsset(tokenHomeChain, tokenHomeAddress);
    }

    function receiveWormholeMessages(
        bytes memory payload,
        bytes[] memory additionalMessages,
        bytes32 sourceAddress,
        uint16 sourceChain,
        bytes32 deliveryHash
    ) external payable {
        Transfer transferType = abi.decode(payload, (Transfer));
        if (transferType == Transfer.TOKEN_BRIDGE) {
            TokenReceived[] memory receivedTokens = new TokenReceived[](
                additionalMessages.length
            );

            for (uint256 i = 0; i < additionalMessages.length; ++i) {
                IWormhole.VM memory parsed = wormhole.parseVM(
                    additionalMessages[i]
                );
                require(
                    parsed.emitterAddress ==
                        tokenBridge.bridgeContracts(parsed.emitterChainId),
                    "Not a Token Bridge VAA"
                );
                ITokenBridge.TransferWithPayload memory transfer = tokenBridge
                    .parseTransferWithPayload(parsed.payload);
                require(
                    transfer.to == toWormholeFormat(address(this)) &&
                        transfer.toChain == wormhole.chainId(),
                    "Token was not sent to this address"
                );

                tokenBridge.completeTransferWithPayload(additionalMessages[i]);

                address thisChainTokenAddress = getTokenAddressOnThisChain(
                    transfer.tokenChain,
                    transfer.tokenAddress
                );
                uint8 decimals = getDecimals(thisChainTokenAddress);
                uint256 denormalizedAmount = transfer.amount;
                if (decimals > 8)
                    denormalizedAmount *= uint256(10) ** (decimals - 8);

                receivedTokens[i] = TokenReceived({
                    tokenHomeAddress: transfer.tokenAddress,
                    tokenHomeChain: transfer.tokenChain,
                    tokenAddress: thisChainTokenAddress,
                    amount: denormalizedAmount,
                    amountNormalized: transfer.amount
                });
            }

            (, bytes memory userPayload) = abi.decode(
                payload,
                (Transfer, bytes)
            );

            // call into overriden method
            receivePayloadAndTokens(
                userPayload,
                receivedTokens,
                sourceAddress,
                sourceChain,
                deliveryHash
            );
        } else if (transferType == Transfer.CCTP) {
            // Currently, 'sendUSDCWithPayloadToEVM' only sends one CCTP transfer
            // That can be modified if the integrator desires to send multiple CCTP transfers
            // in which case the following code would have to be modified to support
            // redeeming these multiple transfers and checking that their 'amount's are accurate
            require(
                additionalMessages.length <= 1,
                "CCTP: At most one Message is supported"
            );

            uint256 amountUSDCReceived;
            if (additionalMessages.length == 1) {
                amountUSDCReceived = redeemUSDC(additionalMessages[0]);
            }

            (, uint256 amount, bytes memory userPayload) = abi.decode(
                payload,
                (Transfer, uint256, bytes)
            );

            // Check that the correct amount was received
            // It is important to verify that the 'USDC' sent in by the relayer is the same amount
            // that the sender sent in on the source chain
            require(amount == amountUSDCReceived, "Wrong amount received");

            receivePayloadAndUSDC(
                userPayload,
                amountUSDCReceived,
                sourceAddress,
                sourceChain,
                deliveryHash
            );
        } else {
            revert("Invalid transfer type");
        }
    }

    // Implement this function to handle in-bound deliveries that include a CCTP transfer
    function receivePayloadAndUSDC(
        bytes memory payload,
        uint256 amountUSDCReceived,
        bytes32 sourceAddress,
        uint16 sourceChain,
        bytes32 deliveryHash
    ) internal virtual {}

    // Implement this function to handle in-bound deliveries that include a TokenBridge transfer
    function receivePayloadAndTokens(
        bytes memory payload,
        TokenReceived[] memory receivedTokens,
        bytes32 sourceAddress,
        uint16 sourceChain,
        bytes32 deliveryHash
    ) internal virtual {}
}

contracts/src/CCTPBase.sol.content:
pragma solidity ^0.8.13;

import "./interfaces/IWormholeReceiver.sol";
import "./interfaces/IWormholeRelayer.sol";
import {IERC20} from "./interfaces/IERC20.sol";
import "./interfaces/CCTPInterfaces/ITokenMessenger.sol";
import "./interfaces/CCTPInterfaces/IMessageTransmitter.sol";

import "./Utils.sol";
import "./Base.sol";

library CCTPMessageLib {
    // The second standardized key type is a CCTP Key
    // representing a CCTP transfer of USDC
    // (on the IWormholeRelayer interface)

    // Note - the default delivery provider only will relay CCTP transfers that were sent
    // in the same transaction that this message was emitted!
    // (This will always be the case if 'CCTPSender' is used)

    uint8 constant CCTP_KEY_TYPE = 2;

    // encoded using abi.encodePacked(domain, nonce)
    struct CCTPKey {
        uint32 domain;
        uint64 nonce;
    }

    // encoded using abi.encode(message, signature)
    struct CCTPMessage {
        bytes message;
        bytes signature;
    }
}

abstract contract CCTPBase is Base {
    ITokenMessenger immutable circleTokenMessenger;
    IMessageTransmitter immutable circleMessageTransmitter;
    address immutable USDC;
    address cctpConfigurationOwner;

    constructor(
        address _wormholeRelayer,
        address _wormhole,
        address _circleMessageTransmitter,
        address _circleTokenMessenger,
        address _USDC
    ) Base(_wormholeRelayer, _wormhole) {
        circleTokenMessenger = ITokenMessenger(_circleTokenMessenger);
        circleMessageTransmitter = IMessageTransmitter(
            _circleMessageTransmitter
        );
        USDC = _USDC;
        cctpConfigurationOwner = msg.sender;
    }
}

abstract contract CCTPSender is CCTPBase {
    uint8 internal constant CONSISTENCY_LEVEL_FINALIZED = 15;

    using CCTPMessageLib for *;

    mapping(uint16 => uint32) public chainIdToCCTPDomain;

    /**
     * Sets the CCTP Domain corresponding to chain 'chain' to be 'cctpDomain'
     * So that transfers of USDC to chain 'chain' use the target CCTP domain 'cctpDomain'
     *
     * This action can only be performed by 'cctpConfigurationOwner', who is set to be the deployer
     *
     * Currently, cctp domains are:
     * Ethereum: Wormhole chain id 2, cctp domain 0
     * Avalanche: Wormhole chain id 6, cctp domain 1
     * Optimism: Wormhole chain id 24, cctp domain 2
     * Arbitrum: Wormhole chain id 23, cctp domain 3
     * Base: Wormhole chain id 30, cctp domain 6
     *
     * These can be set via:
     * setCCTPDomain(2, 0);
     * setCCTPDomain(6, 1);
     * setCCTPDomain(24, 2);
     * setCCTPDomain(23, 3);
     * setCCTPDomain(30, 6);
     */
    function setCCTPDomain(uint16 chain, uint32 cctpDomain) public {
        require(
            msg.sender == cctpConfigurationOwner,
            "Not allowed to set CCTP Domain"
        );
        chainIdToCCTPDomain[chain] = cctpDomain;
    }

    function getCCTPDomain(uint16 chain) internal view returns (uint32) {
        return chainIdToCCTPDomain[chain];
    }

    /**
     * transferUSDC wraps common boilerplate for sending tokens to another chain using IWormholeRelayer
     * - approves the Circle TokenMessenger contract to spend 'amount' of USDC
     * - calls Circle's 'depositForBurnWithCaller'
     * - returns key for inclusion in WormholeRelayer `additionalVaas` argument
     *
     * Note: this requires that only the targetAddress can redeem transfers.
     *
     */

    function transferUSDC(
        uint256 amount,
        uint16 targetChain,
        address targetAddress
    ) internal returns (MessageKey memory) {
        IERC20(USDC).approve(address(circleTokenMessenger), amount);
        bytes32 targetAddressBytes32 = addressToBytes32CCTP(targetAddress);
        uint64 nonce = circleTokenMessenger.depositForBurnWithCaller(
            amount,
            getCCTPDomain(targetChain),
            targetAddressBytes32,
            USDC,
            targetAddressBytes32
        );
        return
            MessageKey(
                CCTPMessageLib.CCTP_KEY_TYPE,
                abi.encodePacked(getCCTPDomain(wormhole.chainId()), nonce)
            );
    }

    // Publishes a CCTP transfer of 'amount' of USDC
    // and requests a delivery of the transfer along with 'payload' to 'targetAddress' on 'targetChain'
    //
    // The second step is done by publishing a wormhole message representing a request
    // to call 'receiveWormholeMessages' on the address 'targetAddress' on chain 'targetChain'
    // with the payload 'abi.encode(amount, payload)'
    // (and we encode the amount so it can be checked on the target chain)
    function sendUSDCWithPayloadToEvm(
        uint16 targetChain,
        address targetAddress,
        bytes memory payload,
        uint256 receiverValue,
        uint256 gasLimit,
        uint256 amount
    ) internal returns (uint64 sequence) {
        MessageKey[] memory messageKeys = new MessageKey[](1);
        messageKeys[0] = transferUSDC(amount, targetChain, targetAddress);

        bytes memory userPayload = abi.encode(amount, payload);
        address defaultDeliveryProvider = wormholeRelayer
            .getDefaultDeliveryProvider();

        (uint256 cost, ) = wormholeRelayer.quoteEVMDeliveryPrice(
            targetChain,
            receiverValue,
            gasLimit
        );

        sequence = wormholeRelayer.sendToEvm{value: cost}(
            targetChain,
            targetAddress,
            userPayload,
            receiverValue,
            0,
            gasLimit,
            targetChain,
            address(0x0),
            defaultDeliveryProvider,
            messageKeys,
            CONSISTENCY_LEVEL_FINALIZED
        );
    }

    function addressToBytes32CCTP(address addr) private pure returns (bytes32) {
        return bytes32(uint256(uint160(addr)));
    }
}

abstract contract CCTPReceiver is CCTPBase {
    function redeemUSDC(
        bytes memory cctpMessage
    ) internal returns (uint256 amount) {
        (bytes memory message, bytes memory signature) = abi.decode(
            cctpMessage,
            (bytes, bytes)
        );
        uint256 beforeBalance = IERC20(USDC).balanceOf(address(this));
        circleMessageTransmitter.receiveMessage(message, signature);
        return IERC20(USDC).balanceOf(address(this)) - beforeBalance;
    }

    function receiveWormholeMessages(
        bytes memory payload,
        bytes[] memory additionalMessages,
        bytes32 sourceAddress,
        uint16 sourceChain,
        bytes32 deliveryHash
    ) external payable {
        // Currently, 'sendUSDCWithPayloadToEVM' only sends one CCTP transfer
        // That can be modified if the integrator desires to send multiple CCTP transfers
        // in which case the following code would have to be modified to support
        // redeeming these multiple transfers and checking that their 'amount's are accurate
        require(
            additionalMessages.length <= 1,
            "CCTP: At most one Message is supported"
        );

        uint256 amountUSDCReceived;
        if (additionalMessages.length == 1) {
            amountUSDCReceived = redeemUSDC(additionalMessages[0]);
        }

        (uint256 amount, bytes memory userPayload) = abi.decode(
            payload,
            (uint256, bytes)
        );

        // Check that the correct amount was received
        // It is important to verify that the 'USDC' sent in by the relayer is the same amount
        // that the sender sent in on the source chain
        require(amount == amountUSDCReceived, "Wrong amount received");

        receivePayloadAndUSDC(
            userPayload,
            amountUSDCReceived,
            sourceAddress,
            sourceChain,
            deliveryHash
        );
    }

    // Implement this function to handle in-bound deliveries that include a CCTP transfer
    function receivePayloadAndUSDC(
        bytes memory payload,
        uint256 amountUSDCReceived,
        bytes32 sourceAddress,
        uint16 sourceChain,
        bytes32 deliveryHash
    ) internal virtual {}
}

